(85, 0, 'rss_57bc725ad6568758915363af670fd8bc', 'O:9:"MagpieRSS":19:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:21:"chsxf on "mCatFilter"";s:4:"link";s:57:"http://wordpress.org/extend/plugins/mcatfilter/#post-6992";s:7:"pubdate";s:31:"Fri, 12 Sep 2008 07:50:07 +0000";s:2:"dc";a:1:{s:7:"creator";s:5:"chsxf";}s:4:"guid";s:41:"6992@http://wordpress.org/extend/plugins/";s:11:"description";s:94:"Excludes categories from The Loop for display on the home page, in feeds and in archive pages.";s:7:"summary";s:94:"Excludes categories from The Loop for display on the home page, in feeds and in archive pages.";}i:1;a:7:{s:5:"title";s:33:"blogologist on "CSS Cache Buster"";s:4:"link";s:63:"http://wordpress.org/extend/plugins/css-cache-buster/#post-6991";s:7:"pubdate";s:31:"Fri, 12 Sep 2008 02:27:26 +0000";s:2:"dc";a:1:{s:7:"creator";s:11:"blogologist";}s:4:"guid";s:41:"6991@http://wordpress.org/extend/plugins/";s:11:"description";s:98:"Always deliver the latest version of your CSS stylesheet to browsers. All caching problems solved.";s:7:"summary";s:98:"Always deliver the latest version of your CSS stylesheet to browsers. All caching problems solved.";}i:2;a:7:{s:5:"title";s:48:"tzafrir on "Wordpress Bookmark Folder Generator"";s:4:"link";s:82:"http://wordpress.org/extend/plugins/wordpress-bookmark-folder-generator/#post-6990";s:7:"pubdate";s:31:"Thu, 11 Sep 2008 22:20:58 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"tzafrir";}s:4:"guid";s:41:"6990@http://wordpress.org/extend/plugins/";s:11:"description";s:150:"This little plugin will output a Firefox bookamrks file with your entire WordPress Dashboard in folders, just as they appear on your panel.\n\nHaving al";s:7:"summary";s:150:"This little plugin will output a Firefox bookamrks file with your entire WordPress Dashboard in folders, just as they appear on your panel.\n\nHaving al";}i:3;a:7:{s:5:"title";s:41:"chuchurocketeer on "Don''t Stop Believing"";s:4:"link";s:66:"http://wordpress.org/ex